mapper.select:
select * from
	(select rownum rnum, b.*
	 from
		(select * from notice
		 order by notice_re_ref desc,
		 notice_re_seq asc) b
	 where rownum < #{end}
	)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update notice
set NOTICE_READCOUNT=NOTICE+READCOUNT+1
where NOTICE_NUM=#{number}
