mapper.update:
update project_board 
set pro_board_bookmark = 1
where pro_board_num = #{pbNum}

update project_board 
set pro_board_bookmark = 0
where pro_board_num = #{pbNum}

update project_board 
set pro_board_like = pro_board_like-1
where PRO_BOARD_NUM = ${pbNum}

update project_board 
set pro_board_like = pro_board_like+1
where PRO_BOARD_NUM = ${pbNum}
